import csvimport numpy as npimport pandas as pdimport matplotlib.pyplot as pltfrom sklearn.linear_model import Ridgefrom sklearn.linear_model import LinearRegressiontrain_file = open('train.csv')test_file = open('test.csv')train = list(csv.reader(train_file))test = list(csv.reader(test_file))train = train[:214] + train[215:]X_train = []y_train = []X_test = []y_test = []for array in list(train)[1:]:    X_train.append(float(array[0]))    y_train.append(float(array[1]))    for array in list(test)[1:]:    X_test.append(float(array[0]))    y_test.append(float(array[1]))X_train = np.array(X_train).reshape(-1, 1)y_train = np.array(y_train)X_test = np.array(X_test).reshape(-1, 1)y_test = np.array(y_test)print(X_train.shape) # 699 data points and 1 featureprint(y_train.shape)# Ridge modelridge = Ridge(alpha=1).fit(X_train, y_train) # Training the ridge modeltrain_score = ridge.score(X_train, y_train)test_score = ridge.score(X_test, y_test)print('Ridge model Training set score: ', train_score)print('Ridge model Test set score: ', test_score)# Linear regression modellr = LinearRegression().fit(X_train, y_train) # Training the linear regression modeltrain_scorelr = lr.score(X_train, y_train)test_scorelr = lr.score(X_test, y_test)print('Linear regression model Training set score: ', train_scorelr)print('linear regression model Test set score: ', test_scorelr)